This example demonstrates how to remove a custom metadata property

using (DocFormat docFormat = new DocFormat(@"C:\input.docx"))
{
    string metadataKey = "CustomKey";
    if (docFormat.DocumentProperties.ContainsKey(metadataKey) && !docFormat.DocumentProperties.IsBuiltIn(metadataKey))
    {
        docFormat.DocumentProperties.Remove(metadataKey);
    }

    docFormat.Save(@"C:\output.docx");
}

